mapper.select:
SELECT boardno, memberno, title, bcontent, rdate, board_cate, file1, file1saved, thumb1, size1
FROM board
WHERE boardno = #{boardno}

mapper.update:
UPDATE board
SET title=#{title}, bcontent = #{bcontent}, board_cate = #{board_cate}
WHERE boardno = #{boardno}

UPDATE board
SET file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE boardno = #{boardno}
