mapper.update:
UPDATE diary
SET title = #{title},
    summary = #{summary},
    weather_code = #{weather_code},
    emno = #{emno}
WHERE diaryno = #{diaryno}

mapper.select:
SELECT diaryno, title, summary, weather_code, emno, ddate
FROM diary
WHERE diaryno = #{diaryno}

SELECT COUNT(*) 
FROM diary 
WHERE 1=1

    AND title LIKE #{title}


    AND ddate >= #{start_date}


    AND ddate <= #{end_date}
