mapper.select:
SELECT emono, type, explan, file1, file1saved, thumb1, size1
FROM emotion
WHERE emono = #{emono}

mapper.update:
UPDATE emotion
SET type=#{type}, explan = #{explan}
WHERE emono = #{emono}

UPDATE emotion
SET file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE emono = #{emono}
