mapper.insert:
INSERT INTO illustration (
  illustno, illust, illust_saved, illust_thumb, illust_size, conversationno, memberno
)
VALUES (
  illustration_seq.nextval, #{illust}, #{illust_saved}, #{illust_thumb}, #{illust_size}, #{conversationno}, #{memberno}
)

mapper.select:
SELECT illustno, illust, illust_saved, illust_thumb, illust_size, conversationno, rdate
FROM illustration
WHERE illustno = #{illustno}

SELECT illustno, illust, illust_saved, illust_thumb, illust_size, conversationno, rdate, r
FROM (
  SELECT illustno, illust, illust_saved, illust_thumb, illust_size, conversationno, rdate, rownum AS r
  FROM (
    SELECT illustno, illust, illust_saved, illust_thumb, illust_size, conversationno, rdate
    FROM illustration
    ORDER BY illustno DESC
  )
  WHERE rownum <= #{end_num}
)
WHERE r >= #{start_num}

SELECT d.ddate
FROM diary d
WHERE d.illustno = #{illustno}

SELECT * FROM (
    SELECT A.*, ROWNUM AS rnum
    FROM (
        SELECT *
        FROM diary
        WHERE 1=1

            AND TRUNC(ddate) >= TO_DATE(#{start_date}, 'YYYY-MM-DD')


            AND TRUNC(ddate) <= TO_DATE(#{end_date}, 'YYYY-MM-DD')

        ORDER BY ddate DESC
    ) A
    WHERE ROWNUM <= #{endNum}
)
WHERE rnum >= #{startNum}
