mapper.insert:
INSERT INTO illustration (
  illustno, illust, illust_saved, illust_thumb, illust_size, conversationno, rdate
)
VALUES (
  illustration_seq.nextval, #{illust}, #{illust_saved}, #{illust_thumb}, #{illust_size}, #{conversationno}, sysdate
)

mapper.select:
SELECT illustno, illust, illust_saved, illust_thumb, illust_size, conversationno, rdate
FROM illustration
WHERE illustno = #{illustno}

SELECT illustno, illust, illust_saved, illust_thumb, illust_size, conversationno, rdate, r
FROM (
  SELECT illustno, illust, illust_saved, illust_thumb, illust_size, conversationno, rdate, rownum AS r
  FROM (
    SELECT illustno, illust, illust_saved, illust_thumb, illust_size, conversationno, rdate
    FROM illustration
    ORDER BY illustno DESC
  )
  WHERE rownum <= #{end_num}
)
WHERE r >= #{start_num}
