mapper.select:
SELECT logno, memberno, table_name, action, ldate, details, ip, is_success
FROM log
WHERE logno = #{logno}

SELECT COUNT(*) 
FROM log 
WHERE 1=1

    AND table_name LIKE #{table_name}


    AND action LIKE #{action}


    AND ip LIKE #{ip}


    AND is_success LIKE #{is_success}


    AND ldate >= #{start_date}


    AND ldate <= #{end_date}
