mapper.select:
SELECT COUNT(id) as cnt
FROM member
WHERE id = #{id}

SELECT memberno, id, passwd, email, name, nickname, birth, zipcode, address1, address2, pf_img, file1saved, thumb1, size1, mdate, grade
FROM member
WHERE memberno = #{memberno}

SELECT memberno, id, passwd, email, name, nickname, birth, zipcode, address1, address2, pf_img, file1saved, thumb1, size1, mdate, grade
FROM member
WHERE id = #{id}

mapper.update:
UPDATE member 
SET name=#{name}, email=#{email}, zipcode=#{zipcode}, address1=#{address1}, address2=#{address2}, pf_img=#{pf_img}
WHERE memberno=#{memberno}

mapper.select:
SELECT COUNT(memberno) as cnt
FROM member
WHERE id=#{id} AND passwd=#{passwd}

SELECT COUNT(memberno) as cnt
FROM member
WHERE memberno=#{memberno} AND passwd=#{passwd}

mapper.update:
UPDATE member
SET passwd=#{passwd}
WHERE memberno=#{memberno}
