mapper.select:
SELECT noticeno, memberno, title, content, goodcnt, cnt, rdate
FROM notice
WHERE noticeno = #{noticeno}

mapper.update:
UPDATE notice
SET goodcnt = goodcnt + 1
WHERE noticeno = #{noticeno}

UPDATE notice
SET goodcnt = goodcnt - 1
WHERE noticeno = #{noticeno}

UPDATE notice
SET cnt = cnt + 1
WHERE noticeno = #{noticeno}

UPDATE notice
SET title = #{title}, content = #{content}
WHERE noticeno = #{noticeno}
