mapper.update:
UPDATE surveyitem
SET item_cnt = item_cnt + 1
WHERE itemno = #{itemno}

mapper.select:
SELECT COUNT (*) as cnt
FROM participants
WHERE itemno = #{itemno} AND memberno = #{memberno}

SELECT pno, itemno, memberno, pdate
FROM participants
WHERE pno = #{pno}

SELECT pno, itemno, memberno, pdate
FROM participants
WHERE itemno = #{itemno} AND memberno = #{memberno}

SELECT * FROM ( SELECT p.pno, p.itemno, p.memberno, p.pdate, i.item AS i_item, m.name AS name, m.email AS email, ROW_NUMBER() OVER (ORDER BY p.pno ASC) AS r FROM participants p JOIN surveyitem i ON p.itemno = i.itemno JOIN member m ON p.memberno = m.memberno
UPPER(m.name) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(i.item) LIKE '%' || UPPER(#{word}) || '%'
) WHERE r BETWEEN #{start_row} AND #{end_row}
