mapper.select:
SELECT itemno, surveyno, item_seq, item, item_cnt
FROM surveyitem
WHERE itemno = #{itemno}

mapper.update:
UPDATE surveyitem
SET item_seq = #{item_seq}, item = #{item}, item_cnt = #{item_cnt}
WHERE itemno = #{itemno}

UPDATE surveyitem
SET item_cnt = item_cnt + 1
WHERE itemno = #{itemno}
