mapper.select:
SELECT weatherno, type, explan, file1, file1saved, thumb1, size1
FROM weather
WHERE weatherno = #{weatherno}

mapper.update:
UPDATE weather
SET type=#{type}, explan = #{explan}
WHERE weatherno = #{weatherno}

UPDATE weather
SET file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE weatherno = #{weatherno}
